CREATE PROCEDURE [dbo].[CreateClient]
@Firstname nvarchar(200), @Lastname nvarchar(200), @DriverLicenceNumber nvarchar(20), @PersonalIDCardNumber nvarchar(20), @Birthdate datetime, @Gender char
AS

SET NOCOUNT ON; BEGIN TRY BEGIN TRAN
IF dbo.GetNumberOfActiveReservationsForClient(@ClientID) >= 3 RAISERROR('This client has 3 active reservations. This is the maximum number of active reservations a client can have at once!', 11, 1);
IF dbo.IsVehicleReservedForPeriod(@VehicleId, @ReservationDateFrom, @ReservationDateTo) = 1 RAISERROR('This vehicle is already reserved for this period!', 11, 1);
IF dbo.GetNumberOfReservationsWithSameVehicleTypeForClient(@ClientId, @VehicleId, @ReservationDateFrom, @ReservationDateTo) >= 1 RAISERROR('Two vehicles of the same type can not be reserved at the same time by the same client!', 11, 1);
IF CAST(@ReservationDateFrom AS date) < CAST(@ReservationDateTo AS date) RAISERROR('Ending date of reservation can not be before Starting date of reservation!', 11, 1);
IF dbo.DoesClientExist(@ClientId) = 0 RAISERROR('Client with this ID does not exist!', 11, 1);
IF dbo.DoesVehicleExist(@VehicleId) = 0 RAISERROR('Vehicle with this ID does not exist!', 11, 1);
INSERT INTO [dbo].[Reservation] ([ClientID] ,[VehicleID] ,[ReservationDateFrom] ,[ReservationDateTo] ,[Active]) VALUES (@ClientId ,@VehicleId ,@ReservationDateFrom ,@ReservationDateTo ,1) COMMIT TRAN END TRY BEGIN CATCH IF(@@TRANCOUNT > 0) ROLLBACK TRAN;
THROW; END CATCH
SELECT CONVERT(int, SCOPE_IDENTITY())

SET NOCOUNT ON BEGIN TRY BEGIN TRAN IF dbo.DoesManufacturerExist(@VehicleManufacturerId) = 0 RAISERROR('Vehicle Manufacturer with this ID does not exist!', 11, 1);
IF dbo.DoesVehicleTypeExist(@VehicleTypeId) = 0 RAISERROR('Vehicle type with this ID does not exist!', 11, 1);
INSERT INTO Vehicle (VehicleName, VehicleManufacturerID, VehicleTypeID, Color, DateManufactured, PricePerDay) VALUES (@VehicleName, @VehicleManufacturerID, @VehicleTypeID, @Color, @DateManufactured, @PricePerDay) COMMIT TRAN END TRY BEGIN CATCH IF(@@TRANCOUNT > 0) ROLLBACK TRAN;
THROW; END CATCH
SELECT CONVERT(int, SCOPE_IDENTITY())

SET NOCOUNT ON; BEGIN TRY BEGIN TRAN INSERT INTO VehicleManufacturer (VehicleManufacturerName, VehicleManufacturerDescription) VALUES (@VehicleManufacturerName, @VehicleManufacturerDescription) COMMIT TRAN END TRY BEGIN CATCH IF(@@TRANCOUNT > 0) ROLLBACK TRAN;
THROW; END CATCH
SELECT CONVERT(int, SCOPE_IDENTITY())

SET NOCOUNT ON; BEGIN TRY BEGIN TRAN INSERT INTO VehicleType (VehicleTypeName, VehicleTypeDescription) VALUES (@VehicleTypeName, @VehicleTypeDescription) COMMIT TRAN END TRY BEGIN CATCH IF(@@TRANCOUNT > 0) ROLLBACK TRAN;
THROW; END CATCH
SELECT CONVERT(int, SCOPE_IDENTITY())

SET NOCOUNT ON
BEGIN TRY BEGIN TRAN IF(SELECT COUNT(*) FROM Client WHERE ClientID = @ClientId) < 1 RAISERROR('Client with this ID does not exist!', 11, 1);
DELETE FROM Client WHERE ClientID = @ClientId COMMIT TRAN END TRY BEGIN CATCH IF(@@TRANCOUNT > 0) ROLLBACK TRAN;
THROW; END CATCH

CREATE PROCEDURE [dbo].[DeleteClient]
@ClientId int

AS

SET NOCOUNT ON
BEGIN TRY BEGIN TRAN IF(SELECT COUNT(*) FROM Vehicle WHERE VehicleID = @VehicleId) < 1 RAISERROR('Vehicle with this ID does not exist!', 11, 1);
DELETE FROM Vehicle WHERE VehicleId = @VehicleId COMMIT TRAN END TRY BEGIN CATCH IF(@@TRANCOUNT > 0) ROLLBACK TRAN;
THROW; END CATCH

BEGIN SET NOCOUNT ON
BEGIN TRY BEGIN TRAN IF(SELECT COUNT(*) FROM VehicleManufacturer WHERE VehicleManufacturerID = @VehicleManufacturerId) < 1 RAISERROR('Vehicle Manufacturer with this ID does not exist!', 11, 1);
DELETE FROM VehicleManufacturer WHERE VehicleManufacturerID = @VehicleManufacturerId COMMIT TRAN END TRY BEGIN CATCH IF(@@TRANCOUNT > 0) ROLLBACK TRAN;
THROW; END CATCH END

BEGIN SET NOCOUNT ON
BEGIN TRY BEGIN TRAN IF(SELECT COUNT(*) FROM VehicleType WHERE VehicleTypeID = @VehicleTypeId) < 1 RAISERROR('Vehicle type with this ID does not exist!', 11, 1);
DELETE FROM VehicleType WHERE VehicleTypeID = @VehicleTypeId COMMIT TRAN END TRY BEGIN CATCH IF(@@TRANCOUNT > 0) ROLLBACK TRAN;
THROW; END CATCH END

BEGIN TRY BEGIN TRAN IF(SELECT COUNT(*) FROM VehicleType WHERE VehicleTypeID = @VehicleTypeId) < 1 RAISERROR('Vehicle type with this ID does not exist!', 11, 1);
UPDATE VehicleType SET VehicleTypeName = ISNULL(@VehicleTypeName, VehicleTypeName) , VehicleTypeDescription = ISNULL(@VehicleTypeDescription, VehicleTypeDescription) , ModifiedDate = GETDATE() WHERE VehicleTypeID = @VehicleTypeId COMMIT TRAN END TRY BEGIN CATCH IF(@@TRANCOUNT > 0) ROLLBACK TRAN;
THROW; END CATCH
